        //on load
        private void Form1_Load(object sender, EventArgs e)
        {
            cmd.Connection = con; //select a database to select table
            con.Open();
            cmd.CommandType = CommandType.Text; //to define it is a string
            cmd.CommandTimeout = 15;

            var strSql = "SELECT userName, T1N1toN2, T1N1toN4, T1N1toN5, T1N2toN4, T1N3toN4, T1N4toN7, T1N5toN7, T1N6toN7, T1N7toN8 ";
            strSql += "from tbluserinfo";
            var cmdpassweight = new SqlCommand(strSql, con);
            var readerFormLoad = cmdpassweight.ExecuteReader();

            string[] arrLinkStorage = new string[9];

            while (readerFormLoad.Read())
            {
                if (readerFormLoad["userName"].ToString() == lblUsername.Text)
                {
                    for (int i = 0; i < 9; i++)
                    {
                        arrLinkStorage[i] = readerFormLoad[i + 1].ToString();
                    }
                }
            }

            LblW1to2.Text = arrLinkStorage[0];
            LblW1to4.Text = arrLinkStorage[1];
            LblW1to5.Text = arrLinkStorage[2];
            LblW2to4.Text = arrLinkStorage[3];
            LblW3to4.Text = arrLinkStorage[4];
            LblW4to7.Text = arrLinkStorage[5];
            LblW5to7.Text = arrLinkStorage[6];
            LblW6to7.Text = arrLinkStorage[7];
            LblW7to8.Text = arrLinkStorage[8];

            con.Close();

            groupBox2.SendToBack();

        }

        //back to my acc page
        private void btnMyAcc_Click(object sender, EventArgs e)
        {
            con.Open();
            var strSql = "SELECT userName, userPassword, userDeviceNum, userTypeTopology, userEmail ";
            strSql += "from tbluserinfo";
            var cmdlogin = new SqlCommand(strSql, con);
            var readerMyAcc = cmdlogin.ExecuteReader();
            while (readerMyAcc.Read())
            {
                if (readerMyAcc["userName"].ToString() == lblUsername.Text)
                {
                    MyAccount main = new MyAccount();
                    main.MethodReceiveName = readerMyAcc[0].ToString();
                    main.MethodReceiveDevice = readerMyAcc[2].ToString();
                    main.MethodReceiveTopology = readerMyAcc[3].ToString();
                    main.MethodReceivePassword = readerMyAcc[1].ToString();
                    main.MethodReceiveEmail = readerMyAcc[4].ToString();
                    main.Show();
                    this.Hide();
                    this.Dispose(false);
                    break;
                }
            }
            con.Close();
        }

