        //button initialize
        private void button1_Click(object sender, EventArgs e)
        {
            //intialize all link weight
            int lowest = 0;
            int N1toN2 = 0;
            int N1toN4 = 0;
            int N1toN5 = 0;
            int N2toN4 = 0;
            int N3toN4 = 0;
            int N4toN7 = 0;
            int N5toN7 = 0;
            int N6toN7 = 0;
            int N7toN8 = 0;

            //after clicking initialize, all the highlighted weight label will return to normal
            LblW1to2.BackColor = Color.LemonChiffon;
            LblW1to2.ForeColor = Color.Black;
            LblW1to4.BackColor = Color.LemonChiffon;
            LblW1to4.ForeColor = Color.Black;
            LblW1to5.BackColor = Color.LemonChiffon;
            LblW1to5.ForeColor = Color.Black;
            LblW2to4.BackColor = Color.LemonChiffon;
            LblW2to4.ForeColor = Color.Black;
            LblW3to4.BackColor = Color.LemonChiffon;
            LblW3to4.ForeColor = Color.Black;
            LblW4to7.BackColor = Color.LemonChiffon;
            LblW4to7.ForeColor = Color.Black;
            LblW5to7.BackColor = Color.LemonChiffon;
            LblW5to7.ForeColor = Color.Black;
            LblW6to7.BackColor = Color.LemonChiffon;
            LblW6to7.ForeColor = Color.Black;
            LblW7to8.BackColor = Color.LemonChiffon;
            LblW7to8.ForeColor = Color.Black;

            //converting the weight value from string to integer
            N1toN2 = Convert.ToInt16(LblW1to2.Text);
            N1toN4 = Convert.ToInt16(LblW1to4.Text);
            N1toN5 = Convert.ToInt16(LblW1to5.Text);
            N2toN4 = Convert.ToInt16(LblW2to4.Text);
            N3toN4 = Convert.ToInt16(LblW3to4.Text);
            N4toN7 = Convert.ToInt16(LblW4to7.Text);
            N5toN7 = Convert.ToInt16(LblW5to7.Text);
            N6toN7 = Convert.ToInt16(LblW6to7.Text);
            N7toN8 = Convert.ToInt16(LblW7to8.Text);

            //when both "from combobox" and "to combobox" is filled, then disable everything (becomes view only mode until click "clear")
            if (cboxFrom.Text != "" && cboxTo.Text != "")
            {
                cboxFrom.Enabled = false;
                cboxTo.Enabled = false;
                button1.Enabled = false;
                btnWireValue.Enabled = false;
                cboxWire.Enabled = false;
                tboxNewValue.Enabled = false;
                PBpc1.Enabled = false;
                Lblpc1.Enabled = false;
                PBpc2.Enabled = false;
                Lblpc2.Enabled = false;
                PBpc3.Enabled = false;
                Lblpc3.Enabled = false;
                PBpc4.Enabled = false;
                Lblpc4.Enabled = false;
                PBpc5.Enabled = false;
                Lblpc5.Enabled = false;
                PBpc6.Enabled = false;
                Lblpc6.Enabled = false;
                PBpc7.Enabled = false;
                Lblpc7.Enabled = false;
                PBpc8.Enabled = false;
                Lblpc8.Enabled = false;
            }

            //when cbox "from and "to" is empty
            if (cboxFrom.Text == "" || cboxTo.Text == "")
            {
                MessageBox.Show("Please select 'From' and 'To' first! ", "Error", MessageBoxButtons.OK);
            }

            //when any of the weightage has a value of 0
            //else if (N1toN2 == 0 || N1toN4 == 0 || N1toN5 == 0 || N2toN4 == 0 || N3toN4 == 0 || N4toN7 == 0 || N5toN7 == 0 || N6toN7 == 0 || N7toN8 == 0)
            //{
            //    MessageBox.Show("Cannot have any weigtage = 0", "Error", MessageBoxButtons.OK);
            //}

            //button initialization
            if(cboxFrom.Text == "1" && cboxTo.Text == "2")
            {
                //defining the total weightage variables
                int Atotal1to2;
                int Btotal1to4to2;
                int Ctotal1to5to7to4to2;

                //once initialize, the red "from" and "to" label will appear
                lblupPC1.Visible = true;
                lblupPC1.Text = "From";
                lblupPC2.Visible = true;
                lblupPC2.Text = "To";

                //1-2
                Atotal1to2 = N1toN2;
                //1-4-2
                Btotal1to4to2 = N1toN4 + N2toN4;
                //1-5-7-4-2
                Ctotal1to5to7to4to2 = N1toN5 + N5toN7 + N4toN7 + N2toN4;

                //sorting in array from smallest to biggest
                int[] arr = new int[] {Atotal1to2, Btotal1to4to2, Ctotal1to5to7to4to2};
                int temp;
                for (int i = 0; i < arr.Length - 1; i++)
